import React, { useEffect, useState } from "react";
import logo from "../assets/logo.png";

const Header = () => {
  const [theme, setTheme] = useState("dark");

  useEffect(() => {
    document.documentElement.classList.add(theme);
  }, [theme]);

  return (
    <header>
      <div className="logo">
        <img src={logo} alt="Taskmate Logo"></img>
        <span>TASKMATE</span>
      </div>
      <div className="themeSelector">
        <span
          className="light"
          onClick={() => {
            setTheme("light");
          }}
        ></span>
        <span
          onClick={() => {
            setTheme("medium");
          }}
          className="medium"
        ></span>
        <span
          className="dark active"
          onClick={() => {
            setTheme("dark");
          }}
        ></span>
        <span
          className="gOne"
          onClick={() => {
            setTheme("gOne");
          }}
        ></span>
        <span
          className="gTwo"
          onClick={() => {
            setTheme("gTwo");
          }}
        ></span>
        <span
          className="gThree"
          onClick={() => {
            setTheme("gThree");
          }}
        ></span>
      </div>
    </header>
  );
};

export default Header;
